mapper.select:
select c.cid
from stuclass c,course cs,class_course cc
where cc.week_time like '%${0}%' and cc.time=#{1}

    select c.cid,cs.csid,cc.place,cc.time,cc.semester
    from stuclass c,course cs,class_course cc
    where cc.week_time like '%${0}%' and cc.time=#{1}
		and cc.cid = c.cid and cs.csid=cc.csid and c.cid=#{2}
